using namespace std;

class Solution {
public:
    set<int> pos;
    int check(int steps, int nums) {
        int curr = *pos.begin();
        while (nums > 0) {
            auto it = pos.lower_bound(curr + steps);
            if (it == pos.end())
                return false;
            curr = *it;
            --nums;
        }

        return true;
    }

    int maxDistance(vector<int>& position, int m) {
        for (auto p: position)
            pos.emplace(p);
        int lo = 1, hi = 1000000000, res = 1;
        while (lo <= hi) {
            int mid = (lo + hi) / 2;
            if (check(mid, m-1)) {
                res = mid;
                lo = mid + 1;
            } else
                hi = mid - 1;
        }

        return res;
    }
};

